import Expenses from './component/Expenses/Expenses'
import NewExpense from './component/NewExpense/NewExpense'
import React, {useState} from 'react';

const OLDDATA = [
  {title: "Bag", amount:"180.25", date: new Date(2021,5,12)},
  {title: "Potatoes", amount:"18.85", date: new Date(2021,4,27)},
  {title: "iPAD", amount:"200.00", date: new Date(2021,3,25)}
];
const App = () =>{
  const [data, setData]= useState(OLDDATA);
  const addExpressHandler = (expense) =>{
    setData((preveExpense)=>{
      return preveExpense.concat(expense);
    });
  }
  return (
    <div>
      <NewExpense onAddExpense={addExpressHandler}/>
      <Expenses data = {data}/>

    </div>
  );
}

export default App;
