import ExpensiveItem from "./ExpensiveItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import { useState } from "react";

const Expenses = (props) => {
  const [actualYear, setYear] = useState("2021");
  const selectedChangeYear = (year) => {
    setYear(year);
  };
  return (
    <Card className="expenses">
      <ExpensesFilter actualYear={actualYear} dropChange={selectedChangeYear} />
      {props.data.map((e) => {
          if(e.date.includes("2021")){
              return 
          }
        return (
          <ExpensiveItem key={e.id} title={e.title} amount={e.amount} date={e.date} />
        );
      })}
    </Card>
  );
};
export default Expenses;
