import React, { useState } from "react";
import "./ExpenseForm.css";
const ExpenseForm = (props) => {
  // const [userInput, setUserInput] = useState({
  //     enteredTitle: '',
  //     enteredAmount: '',
  //     enteredDate: ''
  // });

  // const titleChangeHandler = (event) =>{
  //     setUserInput({
  //         ...userInput,
  //         enteredTitle: event.target.value
  //     });
  // };
  // const amountChangeHandler = (event)=>{
  //     setUserInput({
  //         ...userInput,
  //         enteredAmount: event.target.value
  //     });
  // };
  // const dateChangeHandler = (event)=>{
  //     setUserInput({
  //         ...userInput,
  //         enteredDate: event.target.value
  //     });
  // };
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [bool, setBool]= useState(true)
  const titleChangeHandler = (event) => {
    setEnteredTitle(event.target.value);
  };
  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };
  const dateChangeHandler = (event) => {
    
    setEnteredDate(event.target.value);
   
  };

  const submitHandler = (event) => {
    event.preventDefault();
    const expensesData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate.toString()+ " " ),
    };
    console.log(event);
    props.onSaveExpenseData(expensesData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
    
  };
  const buttonIsClicked =(e)=>{
    console.log(bool)
    setBool(false);
}
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" required="true" value={enteredTitle} onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
            required="true"
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
            required="true"
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit" onClick={buttonIsClicked}>Cancel</button>
        <button type="submit" onClick={buttonIsClicked}>Add Expense</button>
      </div>
    </form>
    
  );
};
export default ExpenseForm;
