import ExpensiveItem from "./ExpensiveItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import { useState } from "react";

const Expenses = (props) => {
  const [actualYear, setYear] = useState("todos");
  const [actualData, setData] = useState(props.data);
  
  const filterDataByState =()=>{
    const data = props.filter((e)=>{
      if(e.date.toString().includes(state)){
          return true
      }else if(state ==="All"){
        return true
      }else{
          
          return false
      }      
    });
    setData([...data]);
    return actualData.map((e) => {
      return (
        
        <ExpensiveItem key={e.id} title={e.title} amount={e.amount} date={e.date} />
      );
    })
  }
  
  const selectedChangeState = (state) => {
    setYear(state);
    
  };
  
  return (
    <Card className="expenses">
      <ExpensesFilter actualYear={actualYear} dropChange={selectedChangeState} />
      {actualData.map((e) => {
        return (
          
          <ExpensiveItem key={e.id} title={e.title} amount={e.amount} date={e.date} />
        );
      })}
    </Card>
  );
};
export default Expenses;
