import ExpensiveItem from "./ExpensiveItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import { useState } from "react";

const Expenses = (props) => {
  const [actualState, setState] = useState("todos");
  const [actualData, setData] = useState(props.data);
  
  const filterDataByState =()=>{
    const data = props.data.filter((e)=>{
      if(e.date.toString().includes(actualState)){
          return true
      }else if(actualState ==="All"){
        return true
      }else{
          
          return false
      }      
    })
    setData([...data]);
    
  }
  
  const selectedChangeState = (state) => {
    setState(state);
    filterDataByState()
  };
  
  return (
    <Card className="expenses">
      <ExpensesFilter actualYear={actualState} dropChange={selectedChangeState} />
      { actualData.map((e) => {
        return (<ExpensiveItem key={e.id} title={e.title} amount={e.amount} date={e.date} />);
    })}
    </Card>
  );
};
export default Expenses;
